import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const SinhvienList = () => {
    const [sinhviens, setSinhviens] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchSinhviens();
    }, []);

    const fetchSinhviens = async () => {
        try {
            const response = await axios.get('https://localhost:7289/api/sinhvien');
            setSinhviens(response.data);
        } catch (err) {
            setError('Không thể tải danh sách sinh viên');
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm('Bạn có chắc muốn xóa sinh viên này?')) {
            try {
                await axios.delete(`https://localhost:7289/api/sinhvien/${id}`);
                fetchSinhviens();
            } catch (err) {
                setError('Không thể xóa sinh viên');
            }
        }
    };

    return (
        <div>
            <h2>Danh sách sinh viên</h2>
            {error && <p className="error">{error}</p>}
            <table>
                <thead>
                    <tr>
                        <th>Mã SV</th>
                        <th>Họ tên</th>
                        <th>Mã khoa</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {sinhviens.map((sv) => (
                        <tr key={sv.msv}>
                            <td>{sv.msv}</td>
                            <td>{sv.hoten}</td>
                            <td>{sv.makhoa}</td>
                            <td>
                                <Link to={`/detail/${sv.msv}`}>Xem</Link> |{' '}
                                <Link to={`/edit/${sv.msv}`}>Sửa</Link> |{' '}
                                <button onClick={() => handleDelete(sv.msv)}>Xóa</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default SinhvienList;
