import './App.css';
import { useEffect, useState } from 'react';

function App() {
  const [jobs, setJobs] = useState([]);
  const [contactInfo, setContactInfo] = useState({ email: '', hotline: '' });

  useEffect(() => {
    // Fetch internship jobs from backend API
    fetch('https://api.example.com/internship-jobs')
      .then((response) => response.json())
      .then((data) => setJobs(data))
      .catch((error) => console.error('Error fetching jobs:', error));

    // Fetch contact information from backend API
    fetch('https://api.example.com/contact-info')
      .then((response) => response.json())
      .then((data) => setContactInfo(data))
      .catch((error) => console.error('Error fetching contact info:', error));
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Chương trình thực tập cho sinh viên</h1>
        <p>Chào mừng bạn đến với trang web thực tập dành cho sinh viên!</p>
      </header>
      <main>
        <section>
          <h2>Danh sách công việc thực tập</h2>
          <ul>
            {jobs.length > 0 ? (
              jobs.map((job, index) => <li key={index}>{job}</li>)
            ) : (
              <li>Đang tải danh sách công việc...</li>
            )}
          </ul>
        </section>
        <section>
          <h2>Thông tin liên hệ</h2>
          <p>Email: {contactInfo.email || 'Đang tải...'}</p>
          <p>Hotline: {contactInfo.hotline || 'Đang tải...'}</p>
        </section>
      </main>
      <footer>
        <p>&copy; 2023 Chương trình thực tập. Mọi quyền được bảo lưu.</p>
      </footer>
    </div>
  );
}

export default App;
